import java.util.List;

public class Klient {
    private String imie;
    private List<Produkt> koszyk;

    public Klient(String imie, List<Produkt> koszyk) {
        this.imie = imie;
        this.koszyk = koszyk;
    }

    public void sprawdzRabaty() {
        Odwiedzajacy wizytor = new Odwiedzajacy();
        for (Produkt produkt : koszyk) {
            produkt.accept(wizytor);
        }
        System.out.println("Suma rabat√≥w dla klienta " + imie + ": " + wizytor.getSumaRabatu());
    }
}
